    if (!stats[field] || stats[field].date !== today) {
        // Initialize stats for today
        stats[field] = {
            date: today,
            min: val,
            max: val,
            sum: val,
            count: 1
        };
        updated = true;
    } else {
        // Update existing daily stats
        stats[field].min = Math.min(stats[field].min, val);
        stats[field].max = Math.max(stats[field].max, val);
        stats[field].sum += val;
        stats[field].count += 1;
        updated = true;
    }
});

if (updated) {
